package Surfaces;

import java.awt.Color;

public class Surface {

	private Color color;
	
	public Surface(Color c) {
		this.color = c;
	}

	public Color getColor() {
		// TODO Auto-generated method stub
		return color;
	}

	/**
	 * @param color the color to set
	 */
	public void setColor(Color color) {
		this.color = color;
	}
	
	
	
}
